    res.status(200).json({
      message: "EL USUARIO SE HA MODIFICADO CORRECTAMENTE",
      user: resource.User.user_list(UserT),
    });
  } catch (error) {
    switch (error.constructor) {
      case UsuarioNoEncontrado:
        res.status(404).send({ message: "El usuario no existe." });
        break;
      case UploadAvatarError:
        res.status(500).send({ message: "Ocurri un error al cargar el avatar." });
        break;
      default:
        res.status(500).send({ message: "Ocurri un problema inesperado." });
        console.log(error);
    }
  }
},
    // Mtodo para listar los usuarios
    list: async(req, res) => {
        try {
            var search = req.query.search; // Obtiene el trmino de bsqueda de la consulta
            // Busca usuarios que coincidan con el trmino de bsqueda en nombre, apellido o correo electrnico
            let Users = await models.User.find({
                $or:[
                    {"name": new RegExp(search, "i")},
                    {"surname": new RegExp(search, "i")},
                    {"email": new RegExp(search, "i")},
                ]
            }).sort({'createdAt': -1}); // Ordena los resultados por fecha de creacin en orden descendente

            // Mapea los usuarios encontrados a una forma ms presentable
            Users = Users.map((user) => {
                return resource.User.user_list(user);
            });

            // Responde con la lista de usuarios encontrados
            res.status(200).json({
                users: Users
            });
        } catch (error) {
            // Manejo de errores, responde con un mensaje de error
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error); // Imprime el error en la consola
        }
    },
    // Mtodo para eliminar un usuario
    remove: async(req, res) => {
        try {
            // Elimina el usuario con el ID especificado en la consulta
            await models.User.findByIdAndDelete({_id: req.query._id});
            // Responde con un mensaje confirmando la eliminacin
            res.status(200).json({
                message: "EL USUARIO SE ELIMINO CORRECTAMENTE",
            });
        } catch (error) {
            // Manejo de errores, responde con un mensaje de error
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error); // Imprime el error en la consola
        }
    }
}

