                categorie: resource.Categorie.categorie_list(CategorieT),
            });
        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error);
        }
    },
    list: async(req,res) => {
        try {
            var search = req.query.search;
            let Categories = await models.Categorie.find({
                $or:[
                    {"tittle": new RegExp(search, "i")},
