// Importa el módulo 'bcryptjs' para encriptar contraseñas
import bcrypt from 'bcryptjs'
import models from '../models'
import token from '../services/token'
import resource from '../resources'

// Exporta un objeto con varios métodos para manejar las operaciones CRUD relacionadas con los usuarios
export default {
    // Método para registrar un nuevo usuario
register: async (req, res) => {
    try {
        // Verifica si se proporcionó una contraseña en la solicitud
        if (!req.body.password) {
            return res.status(400).json({
                message: "Falta la contraseña en la solicitud."
            });
        }

        // Encripta la contraseña del usuario
        req.body.password = await bcrypt.hash(req.body.password, 10);

        // Crea un nuevo usuario con los datos del cuerpo de la solicitud
        const user = await models.User.create(req.body);

        // Responde con el objeto del usuario creado
        res.status(200).json(user);
    } catch (error) {
        let errorMessage = "Ocurrió un problema al registrar el usuario.";

        // Agrega detalles del error basado en el tipo de error
        if (error.name === 'ValidationError') {
            errorMessage = "Error de validación de datos. Por favor, verifique los datos enviados.";
        } else if (error.code === 11000) {
            // Detección específica del error de duplicación de clave
            if (error.keyPattern && error.keyPattern.email) {
                errorMessage = "El correo electrónico ya está registrado. Por favor, use un correo electrónico diferente.";
            } else {
                errorMessage = "Ya existe un registro con el mismo valor único. Por favor, use un valor diferente.";
            }
        } else if (error.name === 'MongoNetworkError') {
            errorMessage = "Error de red al conectar con la base de datos. Por favor, intente nuevamente más tarde.";
        } else if (error.message.includes('ECONNREFUSED')) {
            errorMessage = "No se pudo conectar a la base de datos. Por favor, verifique que el servidor de base de datos esté en funcionamiento.";
        }
    // Manejo de errores, responde con un mensaje de error específico
        res.status(500).send({
            message: errorMessage,
            error: error.message // Incluye el mensaje de error para más detalles
        });
        console.log(error); // Imprime el error en la consola
    }
},
// Método para registrar un nuevo administrador
register_admin: async (req, res) => {
  try {
    // Validación de entrada
    const { error } = validateAdminData(req.body);
    if (error) {
      return res.status(400).send({ message: error.details[0].message });
    }

    // Verifica si el usuario ya existe por su correo electrónico
    const userV = await models.User.findOne({ email: req.body.email });
    if (userV) {
      return res.status(400).send({ message: "El correo electrónico ya está en uso." });
    }

    // Establece el rol del usuario como "admin" y encripta la contraseña
    req.body.rol = "admin";
    req.body.password = await bcrypt.hash(req.body.password, 10);

    // Crea un nuevo usuario administrador
    let user = await models.User.create(req.body);

    // Responde con el objeto del usuario creado
    res.status(200).json({ user: resource.User.user_list(user) });
  } catch (error) {
    switch (error.code) {
      case 11000:
        res.status(400).send({ message: "El correo electrónico ya está en uso." });
        break;
      case "ValidationError":
        res.status(400).send({ message: error.errors.map(err => err.message).join(", ") });
        break;
      default:
        const errorId = uuid.v4();
        logger.error(errorId, error);
        res.status(500).send({ message: "Ocurrió un problema inesperado.", errorId });
    }
  }
},
    // Método para iniciar sesión
    login: async(req, res) => {
        try {
            // Busca al usuario por su correo electrónico y estado activo
            const user = await models.User.findOne({email: req.body.email, state: 1});
            if(user){
                // Verifica si la contraseña es correcta
                let compare = await bcrypt.compare(req.body.password, user.password);
                if(compare){
                    // Genera un token para el usuario
                    let tokenT = await token.encode(user._id, user.rol, user.email);

                    // Prepara la respuesta con el token y los datos del usuario
                    const USER_FRONTED = {
                        token: tokenT,
                        user: {
                            name: user.name,
                            email: user.email,
                            surname: user.surname,
                            avatar: user.avatar,
                        },
                    }

                    // Responde con los datos del usuario y el token
                    res.status(200).json({
                        USER_FRONTED: USER_FRONTED,
                    })
                } else {
                    // Responde con un mensaje de error si la contraseña es incorrecta
                    res.status(500).send({
                        message: "LA CONTRASEÑA ES INCORRECTA"
                    });
                }
            } else {
                // Responde con un mensaje de error si el usuario no existe
                res.status(500).send({
                    message: "EL USUARIO NO EXISTE"
                });
            }
        } catch (error) {
            // Manejo de errores, responde con un mensaje de error
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error); // Imprime el error en la consola
        }
    },
    // Método para iniciar sesión como administrador
    login_admin: async(req, res) => {
        try {
            // Busca al usuario por su correo electrónico, estado activo y rol de administrador
            const user = await models.User.findOne({email: req.body.email, state: 1, rol: "admin"});
            if(user){
                // Verifica si la contraseña es correcta
                let compare = await bcrypt.compare(req.body.password, user.password);
                if(compare){
                    // Genera un token para el administrador
                    let tokenT = await token.encode(user._id, user.rol, user.email);

                    // Prepara la respuesta con el token y los datos del administrador
                    const USER_FRONTED = {
                        token: tokenT,
                        user: {
                            name: user.name,
                            email: user.email,
                            surname: user.surname,
                            avatar: user.avatar,
                            rol: user.rol,
                        },
                    }

                    // Responde con los datos del administrador y el token
                    res.status(200).json({
                        USER_FRONTED: USER_FRONTED,
                    })
                } else {
                    // Responde con un mensaje de error si la contraseña es incorrecta
                    res.status(500).send({
                        message: "LA CONTRASEÑA ES INCORRECTA"
                    });
                }
            } else {
                // Responde con un mensaje de error si el administrador no existe
                res.status(500).send({
                    message: "EL USUARIO NO EXISTE"
                });
            }
        } catch (error) {
            // Manejo de errores, responde con un mensaje de error
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error); // Imprime el error en la consola
        }
    },
   update: async (req, res) => {
  try {
    // Validación de entrada
    const { error } = validateUserData(req.body);
    if (error) {
      return res.status(400).send({ message: error.details[0].message });
    }

    // Si hay archivos en la solicitud, procesa la imagen de avatar
    if (req.files) {
      const avatar = await processAvatar(req.files.avatar);
      if (!avatar) {
        throw new UploadAvatarError();
      }
      req.body.avatar = avatar;
    }

    // Si hay una contraseña en el cuerpo de la solicitud, encripta la nueva contraseña
    if (req.body.password) {
      req.body.password = await bcrypt.hash(req.body.password, 10);
    }

    // Actualiza el usuario con el ID especificado en el cuerpo de la solicitud
    await models.User.findByIdAndUpdate({ _id: req.body._id }, req.body);

    // Obtiene el usuario actualizado
    const UserT = await models.User.findOne({ _id: req.body._id });

    // Responde con un mensaje y el usuario actualizado
    res.status(200).json({
      message: "EL USUARIO SE HA MODIFICADO CORRECTAMENTE",
      user: resource.User.user_list(UserT),
    });
  } catch (error) {
    switch (error.constructor) {
      case UsuarioNoEncontrado:
        res.status(404).send({ message: "El usuario no existe." });
        break;
      case UploadAvatarError:
        res.status(500).send({ message: "Ocurrió un error al cargar el avatar." });
        break;
      default:
        res.status(500).send({ message: "Ocurrió un problema inesperado." });
        console.log(error);
    }
  }
},
    // Método para listar los usuarios
    list: async(req, res) => {
        try {
            var search = req.query.search; // Obtiene el término de búsqueda de la consulta
            // Busca usuarios que coincidan con el término de búsqueda en nombre, apellido o correo electrónico
            let Users = await models.User.find({
                $or:[
                    {"name": new RegExp(search, "i")},
                    {"surname": new RegExp(search, "i")},
                    {"email": new RegExp(search, "i")},
                ]
            }).sort({'createdAt': -1}); // Ordena los resultados por fecha de creación en orden descendente

            // Mapea los usuarios encontrados a una forma más presentable
            Users = Users.map((user) => {
                return resource.User.user_list(user);
            });

            // Responde con la lista de usuarios encontrados
            res.status(200).json({
                users: Users
            });
        } catch (error) {
            // Manejo de errores, responde con un mensaje de error
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error); // Imprime el error en la consola
        }
    },
    // Método para eliminar un usuario
    remove: async(req, res) => {
        try {
            // Elimina el usuario con el ID especificado en la consulta
            await models.User.findByIdAndDelete({_id: req.query._id});
            // Responde con un mensaje confirmando la eliminación
            res.status(200).json({
                message: "EL USUARIO SE ELIMINO CORRECTAMENTE",
